
function capitalizeBrandAndModel() {
    var brandValue = "";
    var modelValue = "";
    brandValue = document.getElementById("validationCustom01").value;
    document.getElementById("validationCustom01").value = capitalize(brandValue);
    modelValue = document.getElementById("validationCustom02").value;
    document.getElementById("validationCustom02").value = capitalize(modelValue);   
}

function capitalize(word) {
    if (word != "")
        return word[0].toUpperCase() + word.slice(1);
    else
        return "";
}
